// Fireplace.js
import React, { useState } from 'react';
import './stone.css'; // Import CSS file for styling


const fireplaceImages = [
  {
    image: 'fireplace1.jpg',
    title: 'Fire Place 01',

  },
  {
    image: 'fireplace2.jpg',
    title: 'Fire Place 02',
  },
  {
    image: 'fireplace3.jpg',
    title: 'Fire Place 03',

  },
  {
    image: 'fireplace4.jpg',
    title: 'Fire Place 04',

  },
  {
    image: 'fireplace5.jpg',
    title: 'Fire Place 05',

  },
  {
    image: 'fireplace6.jpg',
    title: 'Fire Place 06',

  },
];

import axios from 'axios'; // Import axios library for making HTTP requests

function Bowls() {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const handleSubmitOrder = async (e) => {
    e.preventDefault();
    
    // Prepare order data
    const orderData = {
      name: e.target.elements.name.value,
      email: e.target.elements.email.value,
      mobile_number: e.target.elements.mobile_number.value,
      required_size: e.target.elements.required_size.value,
      quantity: e.target.elements.quantity.value
    };

    try {
      // Make HTTP POST request to submit the order
      const response = await axios.post('/api/submit_order/', orderData);

      // Log success message and clear selectedImage after submitting order
      console.log('Order submitted successfully:', response.data);
      setSelectedImage(null);
    } catch (error) {
      // Log error message if request fails
      console.error('Error submitting order:', error);
    }
  };

  return (
    <div className="right-container">
      <div className="row">
        {fireplaceImages.map((product, index) => (
          <div key={index} className="col-md-4 mb-5" onClick={() => handleImageClick(product)}>
            <img src={product.image} alt={product.title} className="stone-img" />
            <h5>{product.title}</h5>
          </div>
        ))}
      </div>
      {/* Modal for ordering */}
      {selectedImage && (
        <div className="modal" onClick={() => setSelectedImage(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close" onClick={() => setSelectedImage(null)}>&times;</span>
            <div className="order-info">
              <h2>Order {selectedImage.title}</h2>
              <form onSubmit={handleSubmitOrder}>
                <div className="form-group">
                  <label>Name:</label>
                  <input type="text" name="name" className="form-control" placeholder="Enter your name" required />
                </div>
                <div className="form-group">
                  <label>Email:</label>
                  <input type="email" name="email" className="form-control" placeholder="Enter your email" required />
                </div>
                <div className="form-group">
                  <label>Mobile Number:</label>
                  <input type="tel" name="mobile_number" className="form-control" placeholder="Enter your mobile number" required />
                </div>
                <div className="form-group">
                  <label>Required Size:</label>
                  <input type="text" name="required_size" className="form-control" placeholder="Enter required size" required />
                </div>
                <div className="form-group">
                  <label>Quantity:</label>
                  <input type="number" name="quantity" className="form-control" placeholder="Enter quantity" required />
                </div>
                <button type="submit" className="btn btn-primary">Submit Order</button>
              </form>
            </div>
            <div className="order-image">
              <img src={selectedImage.image} alt={selectedImage.title} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Bowls;
