// Granite.js
import axios from 'axios';
import React, { useState } from 'react';
import './stone.css'; // Import CSS file for styling

const solidworkImages = [
  {
    image: 'solid1.jpg',
    title: 'Solid Work 1',

  },
  {
    image: 'solid2.jpg',
    title: 'Solid Work 2',
  },
  {
    image: 'solid3.jpg',
    title: 'Solid Work 3',

  },
  {
    image: 'solid4.jpg',
    title: 'Solid Work 4',

  },
  {
    image: 'solid5.jpg',
    title: 'Solid Work 5',

  },
  {
    image: 'solid6.jpg',
    title: 'Solid Work 6',

  },
];

function SolidWorks() {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };
 const handleSubmitOrder = (e) => {
  e.preventDefault();

  // Prepare the data from form elements
  const orderData = {
    name: e.target.elements.name.value,
    email: e.target.elements.email.value,
    mobile_number: e.target.elements.mobile_number.value,
    required_size: e.target.elements.required_size.value,
    quantity: e.target.elements.quantity.value
  };

  axios.post('http://127.0.0.1:8000/orders/', orderData, {
    headers: {
      'Content-Type': 'application/json'
    }
  })
  .then(response => {
    console.log('Order submitted successfully:', response.data);
    // Clear selectedImage after submitting order
    if (setSelectedImage) setSelectedImage(null);
    // Consider adding more UI feedback here, like a success message to the user
  })
  .catch(error => {
    console.error('Error submitting order:', error);
    // Consider adding more UI feedback here, like displaying an error message
  });
};


  return (
    <div className="left-container">
      <div className="row">
        {solidworkImages.map((product, index) => (
          <div key={index} className="col-md-4 mb-5" onClick={() => handleImageClick(product)}>
            <img src={product.image} alt={product.title} className="stone-img" />
            <h5>{product.title}</h5>
          </div>
        ))}
      </div>
      {/* Modal for ordering */}
      {selectedImage && (
        <div className="modal" onClick={() => setSelectedImage(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close" onClick={() => setSelectedImage(null)}>&times;</span>
            <div className="order-info">
              <h2>Order {selectedImage.title}</h2>
              <form onSubmit={handleSubmitOrder}>
                <div className="form-group">
                  <label>Name:</label>
                  <input type="text" className="form-control" placeholder="Enter your name" required />
                </div>
                <div className="form-group">
                  <label>Email:</label>
                  <input type="email" className="form-control" placeholder="Enter your email" required />
                </div>
                <div className="form-group">
                  <label>Mobile Number:</label>
                  <input type="tel" className="form-control" placeholder="Enter your mobile number" required />
                </div>
                <div className="form-group">
                  <label>Required Size:</label>
                  <input type="text" className="form-control" placeholder="Enter required size" required />
                </div>
                <div className="form-group">
                  <label>Quantity:</label>
                  <input type="number" className="form-control" placeholder="Enter quantity" required />
                </div>
                <button type="submit" className="btn btn-primary">Send Email</button>
              </form>
            </div>
            <div className="order-image">
              <img src={selectedImage.image} alt={selectedImage.title} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SolidWorks;
