import React, { useState } from "react";


export default function Navbar() {
    const [activeLink, setActiveLink] = useState(""); // State to track the active link
    const [isMarbleCraftsHovered, setIsMarbleCraftsHovered] = useState(false);
  
    const handleLinkClick = (link) => {
      setActiveLink(link);
      setIsMarbleCraftsHovered(false);
    };
  
    const handleMouseEnter = () => {
      setIsMarbleCraftsHovered(true);
    };
  
    const handleMouseLeave = () => {
      setIsMarbleCraftsHovered(false);
    };
  
    return (
      <nav>
        <h3
          onClick={() => handleLinkClick("Home")}
          className={activeLink === "Home" ? "active" : ""}
        >
          Home
        </h3>
        <div
          className={`dropdown ${isMarbleCraftsHovered ? "open" : ""}`}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          <h3
            className={activeLink === "MarbleCrafts" ? "active" : ""}
          >
            Marble Crafts
          </h3>
          {isMarbleCraftsHovered && (
            <div className="dropdown-content">
              <h4 onClick={() => handleLinkClick("LocalMarble")}>Local Marble</h4>
              <h4 onClick={() => handleLinkClick("ImportedMarble")}>Imported Marble</h4>
            </div>
          )}
        </div>
        <h3
        onClick={() => handleLinkClick("MarbleCollection")}
        className={activeLink === "MarbleCollection" ? "active" : ""}
      >
        Marble Collection
      </h3>
      <h3
        onClick={() => handleLinkClick("OnyxCrafts")}
        className={activeLink === "OnyxCrafts" ? "active" : ""}
      >
        Onyx Crafts
      </h3>
      <h3
        onClick={() => handleLinkClick("OnyxCollection")}
        className={activeLink === "OnyxCollection" ? "active" : ""}
      >
        Onyx Collection
      </h3>
      <h3
        onClick={() => handleLinkClick("SaltHandicraft")}
        className={activeLink === "SaltHandicraft" ? "active" : ""}
      >
        Salt Handicraft
      </h3>
      <h3
        onClick={() => handleLinkClick("Contact")}
        className={activeLink === "Contact" ? "active" : ""}
      >
        Contact
      </h3>
    </nav>
  );
}
